import React, { useState } from 'react';

function App() {

  const [todo, setTodo] = useState();



  const handleFormSubmit = (e) => {
    e.preventDefault();
    setTodos([
      ...todos,
      todo
    ]);
  }

  const handleInputChange =  (e) => {
    setTodo({
      ...todo,
      id: todos.length + 1,
      title: e.target.value,
      done: false
    })
  }

  return (
    <div>
      {
        todos.map(i => (
        <div key={i.id}>{i.title}
        <hr></hr>
        </div>
        ))
      }
      <form onSubmit={handleFormSubmit}>
        <input type="text" name="todo" id="title" placeholder="Add todo" onChange={handleInputChange}/> 
        <button>ADD</button>
      </form>
    </div>
  );
}

export default App;
