import React, { useState } from 'react';

function App() {
  const [todos, setTodos] = useState([
    {id: 1, title: "Estudar React", done: true},
    {id: 2, title: "Estudar Angular", done: false},
    {id: 3, title: "Estudar JavaScript", done: true},
    {id: 4, title: "Estudar TypeScript", done: true},
  ]);

  const handleFormSubmit = (e) => {
    e.preventDefault();
    let value = document.getElementById('todo');

    setTodos({
      ...todos,
      id: todos.length + 1,
      title: value.target.value,
      done: false
    })
  }

  return (
    <div>
      {
        todos.map(i => (
        <div key={i.id}>{i.title}
        <hr></hr>
        </div>
        ))
      }
      <form onSubmit={handleFormSubmit}>
        <input id="todo" type="text" name="todo" id="title" placeholder="Add todo"/> 
        <button>ADD</button>
      </form>
    </div>
  );
}

export default App;
